"use strict";
var StateChanger = (function () {
    function StateChanger() {
    }
    StateChanger.prototype.onRequestCurrentState = function (cb) {
        this.requestHandler = cb;
    };
    StateChanger.prototype.onTriggerChange = function (cb) {
        this.triggerHandler = cb;
    };
    StateChanger.prototype.triggerChange = function (newState) {
        if (this.triggerHandler) {
            this.triggerHandler(newState);
        }
    };
    StateChanger.prototype.requestStateCopy = function () {
        if (this.requestHandler) {
            return this.requestHandler();
        }
    };
    return StateChanger;
}());
exports.StateChanger = StateChanger;
