import State from './State';
import { StateChanger } from './StateChanger';
export default class StateChangerManager {
    private stateChangers;
    registerStateChanger(stateChanger: StateChanger, currentState: State, onTriggerCB: (changes: {
        [key: string]: any;
    }) => void, onRequestCB: () => State): State;
    unregisterStateChanger(stateChanger: StateChanger): void;
    checkStateKey(currentState: State, stateKey: string, stateChangerName: string): void;
    registeredStateChangers: Array<string>;
}
